// ==UserScript==
// @name         HighlightSearchWikipedia
// @namespace    https://github.com/iiviigames/HighlightSearchWikipedia
// @version      0.1
// @description  After highlighting a term in a webpage, right clicking will show a new option to "Search 'highlighted_text' on Wikipedia".
// @author       iivii | https://odd.codes
// @resource     https://userscripts-mirror.org/scripts/review/151097
// @include      *
// @exclude      file://*
// @grant        GM_openInTab
// ==/UserScript==

// ===================================================
/* 					EVENT LISTENERS					*/
// ===================================================


if (!("contextMenu" in document.documentElement &&
      "HTMLMenuItemElement" in window)) return;

var body = document.body;
body.addEventListener("contextmenu", updateContextMenu, false);

var menu = body.appendChild(document.createElement("menu"));
menu.outerHTML = '<menu id="userscript-search-highlight" type="context"><menuitem label="Search Wikipedia for Highlighted Terms" icon="data:image/png;base64,"></menuitem></menu>';

document.querySelector("#userscript-search-highlight menuitem").addEventListener("click", searchWiki, false);


// ===================================================
/* 						FUNCTIONS					*/
// ===================================================


// Get the currently highlighted text on the page.
function highlightedText(){
    var txtnode = document.getSelection();
    var txt = txtnode.toString();
    return txt;
}


// Checks if the element of the page that received a right click actually contains text within it.
// This must contain not just text, but also HIGHLIGHTED text.
function updateContextMenu(check) {
    // Executed when user right click on web page body
    // check.target is the element you right click on
    var node = check.target;
    var item = document.querySelector("#userscript-search-highlight menuitem");
    var query = highlightedText();
    if (node.textContent != "" && query != "") {
        body.setAttribute("contextmenu", "userscript-search-highlight");
        item.setAttribute("searchTerm", query);
    } else {
        body.removeAttribute("contextmenu");
        item.removeAttribute("searchTerm");
    }
}


// Performs a new tab search to Wikipedia when the right click option is used.
function searchWiki(check) {
    // This only fires when the right click menu is used.
    // check.target is the <menuitem> element
    var searchTerm = check.target.getAttribute("searchTerm");
    GM_openInTab("https://en.wikipedia.org/wiki/Special:Search/" + searchTerm);
}


// ===================================================
/* 						UNUSED						*/
// ===================================================


// Gets selected text from window
function getSelectedText() {
    var selectedText = '';
    // Get Window Selection
    if (window.getSelection) {
        selectedText = window.getSelection();
    }
    // Get Document Selection
    else if (document.getSelection) {
        selectedText = document.getSelection();
    }
    // Document Selection
    else if (document.selection) {
        selectedText = document.selection.createRange().text;
    } // else return;
    // To write the selected text into the textarea
    //document.testform.selectedtext.value = selectedText;
    if (selectedText == '') {
//         throw 'Nothing highlighted';
        return;
    } else {
        return selectedText;
    }
};
